import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Header.css';
import '../styles/scrollAnimations.css';
import { useStickyHeader } from '../hooks/useScrollEffects';

const Header = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { isScrolled, isVisible } = useStickyHeader();

  const handleLogoClick = (e) => {
    e.preventDefault();
    navigate('/');
    // Immer zum Seitenanfang scrollen nach kurzer Verzögerung
    setTimeout(() => {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }, 100);
  };

  const handleHomeClick = (e) => {
    e.preventDefault();
    navigate('/');
    // Immer zum Seitenanfang scrollen nach kurzer Verzögerung
    setTimeout(() => {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }, 100);
  };

  const handleMenuClick = (e) => {
    e.preventDefault();
    navigate('/menu');
    // Immer zum Seitenanfang scrollen nach kurzer Verzögerung
    setTimeout(() => {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }, 100);
  };

  const handleContactClick = (e) => {
    e.preventDefault();
    navigate('/kontakt');
    // Immer zum Seitenanfang scrollen nach kurzer Verzögerung
    setTimeout(() => {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }, 100);
  };
  return (
    <header 
      className={`main-header ${isScrolled ? 'header-scrolled' : ''} ${!isVisible ? 'header-hidden' : ''}`}
    >
      <div className="header-container">
        {/* Logo - als Home Button */}
        <Link to="/" className="logo" onClick={handleLogoClick}>
          <span className="logo-title">FINEWERK</span>
          <span className="logo-subtitle">
            <span>S</span>
            <span>A</span>
            <span>A</span>
            <span>R</span>
            <span>L</span>
            <span>O</span>
            <span>U</span>
            <span>I</span>
            <span>S</span>
          </span>
        </Link>

        {/* Navigation */}
        <nav className="main-nav">
          <Link to="/" onClick={handleHomeClick}>Home</Link>
          <Link to="/menu" onClick={handleMenuClick}>Speisekarte</Link>
          <Link to="/kontakt" onClick={handleContactClick}>Kontakt</Link>
        </nav>
      </div>
    </header>
  );
};

export default Header;
