import React, { useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './HomePage.css';
import '../styles/scrollAnimations.css';
import { useScrollAnimation, useParallax } from '../hooks/useScrollEffects';

const HomePage = () => {
  const navigate = useNavigate();

  // Scroll Animation Refs
  const [heroRef, heroVisible] = useScrollAnimation({ threshold: 0.3 });
  const [reservationRef, reservationVisible] = useScrollAnimation({ threshold: 0.4 });
  const [voucherRef, voucherVisible] = useScrollAnimation({ threshold: 0.3 });
  const [galleryRef, galleryVisible] = useScrollAnimation({ threshold: 0.2 });
  
  // Parallax für Hero-Section
  const [parallaxRef, parallaxOffset] = useParallax(0.3);

  const handleReservationClick = () => {
    navigate('/kontakt#nachricht-senden');
    // Scroll zur Nachricht-Senden Section nach kurzer Verzögerung
    setTimeout(() => {
      const element = document.getElementById('nachricht-senden');
      if (element) {
        element.scrollIntoView({ behavior: 'smooth', block: 'start' });
      }
    }, 100);
  };
  return (
    <div className="home-page">
      {/* Hero Section - Großes Bild vom Kaffee */}
      <section className="hero-section scroll-snap-section" ref={heroRef}>
        <div className="hero-container">
          <div 
            className={`hero-image-placeholder parallax-element ${heroVisible ? 'visible' : ''}`}
            ref={parallaxRef}
            style={{
              transform: `translateY(${parallaxOffset}px)`
            }}
          >
            <div className={`hero-content fade-in ${heroVisible ? 'visible' : ''}`}>
              <h1 className="hero-title">FINEWERK SAARLOUIS</h1>
              <p className="hero-subtitle">Feinste Kaffeekultur & Kulinarische Vielfalt</p>
            </div>
          </div>
        </div>
      </section>

      {/* Reservierung Section */}
      <section className="reservation-section scroll-snap-section" ref={reservationRef}>
        <div className="section-container">
          <h2 className={`section-title fade-in ${reservationVisible ? 'visible' : ''}`}>Platz Reservieren</h2>
          <p className={`section-text fade-in ${reservationVisible ? 'visible' : ''}`}>
            Sichern Sie sich Ihren Lieblingsplatz in unserem gemütlichen Kaffee. 
            Kontaktieren Sie uns für eine Reservierung.
          </p>
          <button 
            onClick={handleReservationClick} 
            className={`cta-button scale-in ${reservationVisible ? 'visible' : ''}`}
          >
            Jetzt Reservieren
          </button>
        </div>
      </section>

      {/* Gutschein Section */}
      <section className="voucher-section scroll-snap-section" ref={voucherRef}>
        <div className="section-container">
          <h2 className={`section-title fade-in ${voucherVisible ? 'visible' : ''}`}>Gutscheine</h2>
          <p className={`section-text fade-in ${voucherVisible ? 'visible' : ''}`}>
            Verschenken Sie besondere Momente! Unsere Gutscheine sind das perfekte Geschenk 
            für alle Kaffee- und Genussliebhaber.
          </p>
          <div className="voucher-cards">
            <div className={`voucher-card stagger-item ${voucherVisible ? 'visible' : ''}`}>
              <div className="voucher-image-placeholder"></div>
              <h3>Kaffee Gutschein</h3>
              <p>Perfekt für Kaffeeliebhaber</p>
              <span className="voucher-price">Ab 10€</span>
            </div>
            <div className={`voucher-card stagger-item ${voucherVisible ? 'visible' : ''}`}>
              <div className="voucher-image-placeholder"></div>
              <h3>Menü Gutschein</h3>
              <p>Kulinarische Vielfalt genießen</p>
              <span className="voucher-price">Ab 25€</span>
            </div>
            <div className={`voucher-card stagger-item ${voucherVisible ? 'visible' : ''}`}>
              <div className="voucher-image-placeholder"></div>
              <h3>Erlebnis Gutschein</h3>
              <p>Komplettes Finewerk Erlebnis</p>
              <span className="voucher-price">Ab 50€</span>
            </div>
          </div>
          <button className={`cta-button scale-in ${voucherVisible ? 'visible' : ''}`}>
            Gutscheine Kaufen
          </button>
        </div>
      </section>

      {/* Produktgalerie Section */}
      <section className="gallery-section scroll-snap-section" ref={galleryRef}>
        <div className="section-container">
          <h2 className={`section-title fade-in ${galleryVisible ? 'visible' : ''}`}>Unsere Produkte</h2>
          <p className={`section-text fade-in ${galleryVisible ? 'visible' : ''}`}>
            Entdecken Sie unsere vielfältige Auswahl an hochwertigen Kaffees, 
            frischen Speisen und köstlichen Desserts.
          </p>
          <div className="product-gallery">
            <div className={`gallery-item stagger-item ${galleryVisible ? 'visible' : ''}`}>
              <div className="gallery-image-placeholder lazy-image"></div>
              <h3>Premium Kaffee</h3>
            </div>
            <div className={`gallery-item stagger-item ${galleryVisible ? 'visible' : ''}`}>
              <div className="gallery-image-placeholder lazy-image"></div>
              <h3>Frische Speisen</h3>
            </div>
            <div className={`gallery-item stagger-item ${galleryVisible ? 'visible' : ''}`}>
              <div className="gallery-image-placeholder lazy-image"></div>
              <h3>Hausgemachte Desserts</h3>
            </div>
            <div className={`gallery-item stagger-item ${galleryVisible ? 'visible' : ''}`}>
              <div className="gallery-image-placeholder lazy-image"></div>
              <h3>Eiscreme</h3>
            </div>
            <div className={`gallery-item stagger-item ${galleryVisible ? 'visible' : ''}`}>
              <div className="gallery-image-placeholder lazy-image"></div>
              <h3>Getränke-Vielfalt</h3>
            </div>
            <div className={`gallery-item stagger-item ${galleryVisible ? 'visible' : ''}`}>
              <div className="gallery-image-placeholder lazy-image"></div>
              <h3>Besondere Spezialitäten</h3>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
